import React, { useState } from 'react';
import { useUserContext } from './context/UserContext';

const UserTable: React.FC = () => {
    const { userList, darkMode } = useUserContext();
    const [expandedUserId, setExpandedUserId] = useState<number | null>(null);

    return (
        <>
            <div className={`overflow-auto ${darkMode ? 'dark' : ''}`}>
                <table className='min-w-full bg-white dark:bg-gray-800'>
                    <thead>
                        <tr>
                            <th className='p-2 text-left'>ID</th>
                            <th className='p-2 text-left'>Name</th>
                            <th className='p-2 text-left'>Email</th>
                            <th className='p-2 text-left'>Company</th>
                            <th className='p-2 text-left'>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {userList.map((res) => (
                            <React.Fragment key={res.id}>
                                <tr className='border-t border-gray-200 dark:border-gray-700'>
                                    <td className='p-2'>{res.id}</td>
                                    <td className='p-2'>{res.name}</td>
                                    <td className='p-2'>{res.email}</td>
                                    <td className='p-2'>{res.company.name}</td>
                                    <td className='p-2'>
                                        <button onClick={() => setExpandedUserId(expandedUserId === res.id ? null : res.id)}>
                                            {expandedUserId === res.id ? 'Hide Details' : 'Show Details'}
                                        </button>
                                    </td>
                                </tr>
                                {expandedUserId === res.id && (
                                    <tr className='border-t border-gray-200 dark:border-gray-700'>
                                        <td colSpan={5} className='p-2'>
                                            Address: {res.address.street}
                                            <br />
                                            Phone: {res.phone}
                                        </td>
                                    </tr>
                                )}
                            </React.Fragment>
                        ))}
                    </tbody>
                </table>
            </div>
        </>
    );
};

export default UserTable;
